import React, { useEffect, useState } from "react";
import CommentList from "./components/CommentList";
import SendComment from "./components/SendComment";
import data from "./data.json";
function App() {
  const [currentUser, setCurrentUser] = useState({});
  const [commentsData, setCommentsData] = useState([]);
  const [updateLocalStorage, setUpdateLocalStorage] = useState(false);

  useEffect(() => {
    if (updateLocalStorage) {
      localStorage.setItem("commentsData", JSON.stringify(commentsData));
      setUpdateLocalStorage(false);
    }
  }, [commentsData, updateLocalStorage]);

  useEffect(() => {
    const storedCommentsData = JSON.parse(localStorage.getItem("commentsData"));
    const storedCurrentUser = JSON.parse(
      localStorage.getItem("currentUserData"),
    );
    setCommentsData(storedCommentsData || []);
    setCurrentUser(storedCurrentUser || {});
  }, []);

  return (
    <div className="App">
      <CommentList currentUser={currentUser} comments={commentsData} />
      <SendComment
        currentUser={currentUser}
        comments={commentsData}
        setUpdateLocalStorage={setUpdateLocalStorage}
      />
    </div>
  );
}
