import React, { useEffect, useState } from "react";
import CommentList from "./components/CommentList";
import SendComment from "./components/SendComment";
import data from "./data.json";

function App() {
  const [currentUser, setCurrentUser] = useState(data.currentUser);
  const [commentsData, setCommentsData] = useState(data.comments);

  const deleteComment = (id) => {
    // Filter out both comments and their replies with the specified ID
    const updatedComments = commentsData.filter((comment) => {
      // Filter out the comment if its ID matches
      if (comment.id === id) {
        return false; // Exclude this comment from the updatedComments array
      } else {
        // Filter out any replies with the specified ID
        const updatedReplies = comment.replies.filter(
          (reply) => reply.id !== id,
        );
        // Update the replies array for this comment
        comment.replies = updatedReplies;
        // Include this comment in the updatedComments array
        return true;
      }
    });

    // Update the commentsData state with the updated array
    setCommentsData(updatedComments);
  };

  return (
    <div className="App">
      <CommentList
        currentUser={currentUser}
        comments={commentsData}
        deleteComment={deleteComment}
      />
      <SendComment
        currentUser={currentUser}
        comments={commentsData}
        setCommentsData={setCommentsData}
        deleteComment={deleteComment}
      />
    </div>
  );
}

export default App;
