import React, { useEffect, useState } from "react";
import CommentList from "./components/CommentList";
import SendComment from "./components/SendComment";
import data from "./data.json";

function App() {
  const [currentUser, setCurrentUser] = useState({});
  const [commentsData, setCommentsData] = useState([]);

  useEffect(() => {
    // Store data in localStorage
    localStorage.setItem("commentsData", JSON.stringify(data.comments));
    localStorage.setItem("currentUserData", JSON.stringify(data.currentUser));

    // Retrieve data from localStorage
    const storedCurrentUser = localStorage.getItem("currentUserData");
    const storedCommentsData = localStorage.getItem("commentsData");

    // Parse JSON data if available, otherwise use default data
    setCurrentUser(
      storedCurrentUser ? JSON.parse(storedCurrentUser) : data.currentUser,
    );
    setCommentsData(
      storedCommentsData ? JSON.parse(storedCommentsData) : data.comments,
    );
  }, [localStorage]);

  return (
    <div className="App">
      <CommentList currentUser={currentUser} comments={commentsData} />
      <SendComment currentUser={currentUser} comments={commentsData} />
    </div>
  );
}

export default App;
