import React, { useEffect, useState } from "react";
import CommentList from "./components/CommentList";
import SendComment from "./components/SendComment";
import data from "./data.json";

function App() {
  const [currentUser, setCurrentUser] = useState({});
  const [commentsData, setCommentsData] = useState([]);

  useEffect(() => {
    localStorage.setItem("commentsData", data.comments);
    localStorage.setItem("currentUserData", data.currentUser);
    setCommentsData(localStorage.getItem("commentsData"));
    setCurrentUser(localStorage.getItem("currentUserData"));
  }, [commentsData, currentUser]); // Run whenever commentsData or currentUser changes

  return (
    <div className="App">
      <CommentList currentUser={currentUser} comments={commentsData} />
      <SendComment currentUser={currentUser} comments={commentsData} />
    </div>
  );
}

export default App;
