import Replies from "./Replies";
import { formatDistanceToNow } from "date-fns";

function formatRelativeTime(timestamp) {
  return formatDistanceToNow(new Date(timestamp), { addSuffix: true });
}

const Comment = ({ comment, currentUser }) => {
  const { user, createdAt, content, score, replies } = comment;
  const formattedCreatedAt = formatRelativeTime(createdAt);

  return (
    <>
      <div className="card">
        <div className="card-header">
          <div className="user-avatar">
            <img src={user.image.png} alt={user.username} />
          </div>
          <p className="username">{user.username}</p>
          <p className="createdAt">{formattedCreatedAt}</p>
        </div>
        <p className="content">{content}</p>
        <div className="card-footer">
          <div className="count-button-div">
            <button>
              <img src="./images/icon-plus.svg" alt="plus count button" />
            </button>
            <p className="count">{score}</p>
            <button className="minus-btn">
              <img src="./images/icon-minus.svg" alt="minus count button" />
            </button>
          </div>
          <button className="reply-button">
            <img src="./images/icon-reply.svg" alt="reply button" />
            <p>Reply</p>
          </button>
        </div>
      </div>
      <Replies replies={comment.replies} currentUser={currentUser} />
    </>
  );
};

export default Comment;
