import { useState } from "react";

const Replies = ({
  comment,
  replies,
  currentUser,
  commentsData,
  setCommentsData,
}) => {
  // State to track the edit mode for each reply
  const [editedReplies, setEditedReplies] = useState({});

  const toggleEdit = (replyID) => {
    setEditedReplies((prevEditedReplies) => ({
      ...prevEditedReplies,
      [replyID]: !prevEditedReplies[replyID], // Toggle the edit mode for the specified replyID
    }));
  };

  const updateReply = (replyID) => {
    const updatedComments = commentsData.map((c) => {
      if (c.id === comment.id) {
        const updatedReplies = c.replies.map((r) =>
          r.id === replyID ? { ...r, content: editedContent } : r,
        );
        return { ...c, replies: updatedReplies };
      }
      return c;
    });
    setCommentsData(updatedComments);
    toggleEdit(replyID); // Close edit mode for the reply after updating
  };

  const deleteReply = (replyID) => {
    const updatedComments = commentsData.map((c) => ({
      ...c,
      replies: c.replies.filter((reply) => reply.id !== replyID),
    }));
    setCommentsData(updatedComments);
  };

  return (
    <div className="replies">
      {replies.map((reply) => {
        const { id, user, createdAt, content, score, replyingTo } = reply;
        const isEdited = editedReplies[id] || false; // Check if the reply is in edit mode

        return (
          <div key={id} className="card">
            <div className="card-header">
              <div className="user-avatar">
                <img src={user.image.png} alt={user.username} />
              </div>
              <p className="username">{user.username}</p>
              {reply.username === currentUser.username && (
                <p className="you">you</p>
              )}
              <p className="createdAt">{createdAt}</p>
            </div>
            <p className="content">
              <span className="replyingTo">@{replyingTo}</span>
              {isEdited ? (
                <textarea
                  className="edit-textarea"
                  value={content}
                  onChange={(e) => setEditedContent(e.target.value)}
                ></textarea>
              ) : (
                content
              )}
            </p>
            <div className="card-footer">
              {/* Rest of your code */}
              <button onClick={() => toggleEdit(id)} className="edit-button">
                <img src="./images/icon-edit.svg" alt="edit button" />
                <p>Edit</p>
              </button>
              {/* Rest of your code */}
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default Replies;
