import { useState } from "react";
import Replies from "./Replies";

const Comment = ({ comment, currentUser, deleteComment, setCommentsData }) => {
  const { user, createdAt, content, score } = commentsData;
  const [editActive, setEditActive] = useState(false);
  const [editedContent, setEditedContent] = useState(content);

  const handleUpdate = () => {
    // Map over the existing comments
    const updatedComments = commentsData.map((c) =>
      // Check if the comment's id matches the id of the current comment
      c.id === comments.id
        ? // If it matches, update its content
          { ...c, content: editedContent }
        : // If it doesn't match, keep the comment as it is
          c,
    );
    // Update the comments data state with the updated comments
    setCommentsData(updatedComments);
    // Exit edit mode
    setEditActive(false);
  };

  return (
    <>
      <div className="card">
        <div className="card-header">
          <div className="user-avatar">
            <img src={user.image.png} alt={user.username} />
          </div>
          <p className="username">{user.username}</p>
          <p className="createdAt">{createdAt}</p>
        </div>
        {editActive ? (
          <textarea
            className="edit-textarea"
            value={editedContent}
            onChange={(e) => setEditedContent(e.target.value)}
          ></textarea>
        ) : (
          <p className="content">{content}</p>
        )}
        <div className="card-footer">
          <div className="count-button-div">
            <button>
              <img src="./images/icon-plus.svg" alt="plus count button" />
            </button>
            <p className="count">{score}</p>
            <button className="minus-btn">
              <img src="./images/icon-minus.svg" alt="minus count button" />
            </button>
          </div>
          {currentUser.username === comment.user.username ? (
            <>
              <button
                onClick={() => deleteComment(comment.id)}
                className="delete-button"
              >
                <img src="./images/icon-delete.svg" alt="delete button" />
                <p>Delete</p>
              </button>
              <button
                onClick={() => setEditActive(true)}
                className="edit-button"
              >
                <img src="./images/icon-edit.svg" alt="edit button" />
                <p>Edit</p>
              </button>
            </>
          ) : (
            <button className="reply-button">
              <img src="./images/icon-reply.svg" alt="reply button" />
              <p>reply</p>
            </button>
          )}
          {editActive && (
            <button
              onClick={() => handleUpdate(comment.id)}
              className="update-button"
            >
              update
            </button>
          )}
        </div>
      </div>
      <Replies
        replies={comment.replies}
        currentUser={currentUser}
        deleteComment={deleteComment}
      />
    </>
  );
};

export default Comment;
