import { useState } from "react";

const Replies = ({ replies, currentUser, deleteComment, setCommentsData }) => {
  const [editActive, setEditActive] = useState(false);
  const [editedContent, setEditedContent] = useState("");

  const handleUpdate = (replyId) => {
    // Map over the existing replies
    const updatedReplies = replies.map((reply) =>
      // Check if the reply's id matches the id of the current reply being edited
      reply.id === replyId
        ? // If it matches, update its content
          { ...reply, content: editedContent }
        : // If it doesn't match, keep the reply as it is
          reply,
    );
    // Update the replies data state with the updated replies
    setCommentsData((prevCommentsData) => {
      // Map over the previous comments data
      return prevCommentsData.map((comment) => {
        // Check if the comment contains the reply being edited
        if (comment.replies.find((reply) => reply.id === replyId)) {
          // If the comment contains the reply being edited, update its replies
          return {
            ...comment,
            replies: updatedReplies,
          };
        } else {
          // If the comment does not contain the reply being edited, return the comment as it is
          return comment;
        }
      });
    });
    // Exit edit mode
    setEditActive(false);
  };

  return (
    <div className="replies">
      {replies.map((reply) => {
        const { id, user, createdAt, content, score, replyingTo } = reply;
        return (
          <div key={id} className="card">
            <div className="card-header">
              <div className="user-avatar">
                <img src={user.image.png} alt={user.username} />
              </div>
              <p className="username">{user.username}</p>
              <p className="createdAt">{createdAt}</p>
            </div>
            {editActive && reply.id === editedReplyId ? (
              <textarea
                className="edit-textarea"
                value={editedContent}
                onChange={(e) => setEditedContent(e.target.value)}
              ></textarea>
            ) : (
              <p className="content">
                <span className="replyingTo">@{replyingTo}</span>
                {content}
              </p>
            )}
            <div className="card-footer">
              <div className="count-button-div">
                <button>
                  <img src="./images/icon-plus.svg" alt="plus count button" />
                </button>
                <p className="count">{score}</p>
                <button className="minus-btn">
                  <img src="./images/icon-minus.svg" alt="minus count button" />
                </button>
              </div>
              {currentUser.username === user.username ? (
                <>
                  <button
                    onClick={() => deleteComment(reply.id)}
                    className="delete-button"
                  >
                    <img src="./images/icon-delete.svg" alt="delete button" />
                    <p>Delete</p>
                  </button>
                  <button className="edit-button">
                    <img src="./images/icon-edit.svg" alt="edit button" />
                    <p>Edit</p>
                  </button>
                </>
              ) : (
                <button className="reply-button">
                  <img src="./images/icon-reply.svg" alt="reply button" />
                  <p>Reply</p>
                </button>
              )}
              {editActive && (
                <button
                  onClick={() => handleUpdate(reply.id)}
                  className="update-button"
                >
                  Update
                </button>
              )}
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default Replies;
