import React, { useState } from "react";
import CommentList from "./components/CommentList";
import SendComment from "./components/SendComment";
import data from "./data.json";

function App() {
  const [currentUser, setCurrentUser] = useState(data.currentUser);
  const [commentsData, setCommentsData] = useState(data.comments);

  const deleteComment = (id) => {
    const updatedComments = commentsData.filter((comment) => {
      if (comment.id === id) {
        return false;
      } else {
        const updatedReplies = comment.replies.filter(
          (reply) => reply.id !== id,
        );
        comment.replies = updatedReplies;
        return true;
      }
    });
    setCommentsData(updatedComments);
  };

  const updateComment = (commentId, editedContent) => {
    const updatedComments = commentsData.map((c) =>
      c.id === commentId ? { ...c, content: editedContent } : c,
    );
    setCommentsData(updatedComments);
  };

  return (
    <div className="App">
      <CommentList
        currentUser={currentUser}
        commentsData={commentsData}
        setCommentsData={setCommentsData}
        deleteComment={deleteComment}
        updateComment={updateComment}
      />
      <SendComment
        currentUser={currentUser}
        commentsData={commentsData}
        setCommentsData={setCommentsData}
      />
    </div>
  );
}

export default App;
