import { useState } from "react";

const Replies = ({
  comment,
  replies,
  currentUser,
  commentsData,
  setCommentsData,
}) => {
  const [editedContent, setEditedContent] = useState({});

  const toggleEdit = (replyID) => {
    if (editedContent[replyID] === undefined) {
      // Set initial content for the textarea when toggling edit mode
      const replyToEdit = replies.find((reply) => reply.id === replyID);
      if (replyToEdit) {
        setEditedContent({
          ...editedContent,
          [replyID]: replyToEdit.content,
        });
      }
    }
    // Toggle edit mode
    setEditedContent({
      ...editedContent,
      [replyID]: !editedContent[replyID],
    });
  };

  const deleteReply = (replyID) => {
    const updatedComments = commentsData.map((comment) => ({
      ...comment,
      replies: comment.replies.filter((reply) => reply.id !== replyID),
    }));
    setCommentsData(updatedComments);
  };

  return (
    <div className="replies">
      {replies.map((reply) => {
        const { id, user, createdAt, content, score, replyingTo } = reply;
        const editOpen = editedContent[id] || false;
        return (
          <div key={id} className="card">
            <div className="card-header">
              <div className="user-avatar">
                <img src={user.image.png} alt={user.username} />
              </div>
              <p className="username">{user.username}</p>
              {reply.username === currentUser.username && (
                <p className="you">you</p>
              )}
              <p className="createdAt">{createdAt}</p>
            </div>
            <p className="content">
              <span className="replyingTo">@{replyingTo}</span>
              {editOpen ? (
                <textarea
                  className="edit-textarea"
                  value={editedContent}
                  onChange={(e) => setEditedContent(e.target.value)}
                ></textarea>
              ) : (
                content
              )}
            </p>
            <div className="card-footer">
              <div className="count-button-div">
                <button>
                  <img src="./images/icon-plus.svg" alt="plus count button" />
                </button>
                <p className="count">{score}</p>
                <button className="minus-btn">
                  <img src="./images/icon-minus.svg" alt="minus count button" />
                </button>
              </div>
              {currentUser.username === user.username ? (
                <>
                  <button
                    onClick={() => deleteReply(reply.id)}
                    className="delete-button"
                  >
                    <img src="./images/icon-delete.svg" alt="delete button" />
                    <p>Delete</p>
                  </button>
                  <button
                    onClick={() => toggleEdit(id)}
                    className="edit-button"
                  >
                    <img src="./images/icon-edit.svg" alt="edit button" />
                    <p>Edit</p>
                  </button>
                </>
              ) : (
                <button className="reply-button">
                  <img src="./images/icon-reply.svg" alt="reply button" />
                  <p>Reply</p>
                </button>
              )}
              {editOpen && (
                <button
                  onClick={() => updateReply(reply.id)}
                  className="update-button"
                >
                  update
                </button>
              )}
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default Replies;
