import React, { useState } from "react";

import { formatDistanceToNow } from "date-fns";

function formatRelativeTime(timestamp) {
  return formatDistanceToNow(new Date(timestamp), { addSuffix: true });
}

const SendComment = ({ currentUser, commentsData, setCommentsData }) => {
  const [newReply, setNewReply] = useState("");

  const sendReply = (commentId) => {
    try {
      const targetComment = commentsData.find(
        (comment) => commentId === comment.id,
      );
      if (!targetComment) return;

      const newReply = {
        id: targetComment.replies.length + 1,
        content: newReply,
        createdAt: formatRelativeTime(new Date()),
        replyingTo: targetComment.user.usernam,
        score: 0,
        user: currentUser,
      };

      const updatedComments = commentsData.map((comment) =>
        comment.id === commentId
          ? { ...comment, replies: [...comment.replies, newReply] }
          : comment,
      );
      setCommentsData(updatedComments);
      setNewReply("");
    } catch (error) {
      console.error("Error sending comment:", error);
    }
  };

  return (
    <div className="send-comment card">
      <textarea
        placeholder="Add a comment..."
        value={newReply}
        onChange={(e) => setNewReply(e.target.value)}
      ></textarea>
      <div className="card-footer">
        <img
          src={currentUser.image?.png}
          className="current-avatar"
          alt="user avatar"
        />
        <button onClick={() => sendReply(commentId)} className="send-button">
          reply
        </button>
      </div>
    </div>
  );
};

export default SendComment;
