import { useState } from "react";

const Reply = ({
  reply,
  currentUser,
  deleteComment,
  setCommentsData,
  commentsData,
}) => {
  const [editOpen, setEditOpen] = useState(false);
  const [editedContent, setEditedContent] = useState(reply.content);

  const updateReply = () => {
    setCommentsData((prevCommentsData) =>
      prevCommentsData.map((comment) => ({
        ...comment,
        replies: comment.replies.map((r) =>
          r.id === reply.id ? { ...r, content: editedContent } : r,
        ),
      })),
    );
    setEditOpen(false);
  };

  const deleteReply = () => {
    // Filter out the deleted reply
    const updatedReplies = commentsData.map((comment) => ({
      ...comment,
      replies: comment.replies.filter((r) => r.id !== reply.id),
    }));
    setCommentsData(updatedReplies);
  };

  return (
    <div className="card">
      <div className="card-header">
        <div className="user-avatar">
          <img src={reply.user.image} alt={reply.user.username} />
        </div>
        <p className="username">{reply.user.username}</p>
        {reply.user.username === currentUser.username && (
          <p className="you">you</p>
        )}
        <p className="createdAt">{reply.createdAt}</p>
      </div>
      {editOpen ? (
        <textarea
          className="edit-textarea"
          value={editedContent}
          onChange={(e) => setEditedContent(e.target.value)}
        ></textarea>
      ) : (
        <p className="content">{reply.content}</p>
      )}
      <div className="card-footer">
        <div className="count-button-div">
          <button>
            <img src="./images/icon-plus.svg" alt="plus count button" />
          </button>
          <p className="count">{reply.score}</p>
          <button className="minus-btn">
            <img src="./images/icon-minus.svg" alt="minus count button" />
          </button>
        </div>
        {currentUser.username === reply.user.username && (
          <>
            <button onClick={deleteReply} className="delete-button">
              <img src="./images/icon-delete.svg" alt="delete button" />
              <p>Delete</p>
            </button>
            <button onClick={() => setEditOpen(true)} className="edit-button">
              <img src="./images/icon-edit.svg" alt="edit button" />
              <p>Edit</p>
            </button>
          </>
        )}
        <button className="reply-button">
          <img src="./images/icon-reply.svg" alt="reply button" />
          <p>Reply</p>
        </button>
        {editOpen && (
          <button onClick={updateReply} className="update-button">
            Update
          </button>
        )}
      </div>
    </div>
  );
};
