const DeleteModal = ({ setModalOpen, commentID, deleteComment }) => {
  const handleReplyOrDelete = (id, reply) => {
    if (reply) {
      // If reply is provided, it means it's a new reply
      const newReply = {
        id: comment.replies.length + 1,
        content: replyContent,
        createdAt: formatRelativeTime(Date.now()),
        replyingTo: reply.user.username,
        score: 0,
        user: currentUser,
      };
      const updatedComments = commentsData.map((c) =>
        c.id === comment.id ? { ...c, replies: [...c.replies, newReply] } : c,
      );
      setCommentsData(updatedComments);
      setOpenReplyID(null);
      setReplyContent("");
    } else {
      // If reply is not provided, it means it's a comment deletion
      const updatedComments = commentsData.filter((comment) => {
        if (comment.id === id) {
          return false;
        } else {
          const updatedReplies = comment.replies.filter(
            (reply) => reply.id !== id,
          );
          comment.replies = updatedReplies;
          return true;
        }
      });
      setCommentsData(updatedComments);
      setModalOpen(false);
    }
  };

  return (
    <div className="modal">
      <h1>Delete comment</h1>
      <p>
        Are you sure you want to delete this comment? This will remove the
        comment and can`t be undone.
      </p>
      <button onClick={() => setModalOpen(false)} className="cancel">
        no, cancel
      </button>
      <button onClick={() => deleteComment(commentID)} className="delete">
        yes, delete
      </button>
    </div>
  );
};

export default DeleteModal;
