import React, { useState } from "react";
import Replies from "./Replies";
import { parse } from "path";

const SendComment = ({ currentUser }) => {
  if (!currentUser || !currentUser.image || !currentUser.image.png) {
    return null;
  }

  const [newCommentContent, setNewCommentContent] = useState("");

  const sendComment = () => {
    const commentsData = localStorage.getItem("commentsData");
    const parsedData = JSON.parse(commentsData);

    const newComment = {
      id: parsedData.length + 1,
      content: newCommentContent,
      createdAt: new Date.UTC(),
      score: 0,
      user: currentUser,
      replies: [],
    };

    parsedData.push(newComment);
    localStorage.setItem("commentsData", JSON.stringify(parsedData));
  };

  return (
    <div className="send-comment card">
      <textarea
        placeholder="Add a comment..."
        value={newCommentContent}
        onChange={(e) => setNewCommentContent(e.target.value)}
      ></textarea>
      <div className="card-footer">
        <img
          src={currentUser.image.png}
          className="current-avatar"
          alt="user avatar"
        />
        <button className="send-button">send</button>
      </div>
    </div>
  );
};

export default SendComment;
